import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

function HomePage() {
  const navigate = useNavigate();
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState('');
  const [showModal] = useState(true);

  const handleCreateRoom = async () => {
    const res = await fetch('http://localhost:3001/create-room', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        isPrivate,
        password: isPrivate ? password : null,
      }),
    });

    const data = await res.json();
    navigate(`/room/${data.roomId}`);
  };

  const handleJoin = () => navigate('/join');

  // CSS Ð¿ÐµÑ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ðµ Ð² JavaScript Ð¾Ð±ÑŠÐµÐºÑ‚Ðµ
  const styles = {
    primaryColor: '#2c3e50',
    accentColor: '#27ae60',
    buttonHover: {
      transform: 'translateY(-2px)',
      boxShadow: '0 4px 15px rgba(231, 76, 60, 0.4)'
    }
  };

  return (
    <div className="container">
      <div className="modal-overlay" style={{ display: showModal ? 'flex' : 'none' }}>
        <div className="modal-content">
          <h1 style={{ color: styles.primaryColor }}>ðŸŽ® Poker Room</h1>
          
          <div style={{ margin: '20px 0' }}>
            <label style={{ display: 'flex', alignItems: 'center', margin: '10px' }}>
              <input
                type="checkbox"
                checked={isPrivate}
                onChange={(e) => setIsPrivate(e.target.checked)}
                style={{ marginRight: '10px' }}
              />
              Private Room
            </label>
            
            {isPrivate && (
              <input
                type="password"
                placeholder="Set password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{ width: '80%' }}
              />
            )}
          </div>

          <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
            <button 
              onClick={handleCreateRoom}
              style={{ 
                backgroundColor: styles.accentColor,
                ...styles.buttonHover
              }}
            >
              ðŸš€ Create Room
            </button>
            <button 
              onClick={handleJoin}
              style={{ 
                backgroundColor: styles.primaryColor,
                ...styles.buttonHover
              }}
            >
              ðŸ”‘ Join Room
            </button>
          </div>
          
          <button 
            onClick={() => navigate('/rules')}
            style={{ 
              marginTop: '20px', 
              background: 'none', 
              color: styles.primaryColor 
            }}
          >
            ðŸ“– How to Play
          </button>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
