// client/src/pages/RoomPage.js
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function RoomPage() {
  const { roomId } = useParams();
  const [players, setPlayers] = useState([]);
  const [cards, setCards] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  useEffect(() => {
    const socket = io('http://localhost:3001');

    // Prompt for player's name and join room
    const playerName = prompt('Enter your name');
    socket.emit('join_room', { roomId, playerName });
    socket.emit('request_room_players', { roomId });

    // Listen for players list updates
    socket.on('room_players', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    // Listen for game start event
    socket.on('game_started', ({ cards }) => {
      setCards(cards);
      setGameStarted(true);
    });

    // Listen for error messages
    socket.on('error_message', ({ message }) => {
      setErrorMsg(message);
    });

    return () => {
      socket.off('room_players');
      socket.off('game_started');
      socket.off('error_message');
      socket.disconnect();
    };
  }, [roomId]);

  const handleStartGame = () => {
    const socket = io('http://localhost:3001');
    socket.emit('start_game', { roomId });
  };

  return (
    <div>
      <h2>Room: {roomId}</h2>

      <h3>Players:</h3>
      <ul>
        {players.map((p) => (
          <li key={p.id}>{p.name}</li>
        ))}
      </ul>

      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}

      {!gameStarted ? (
        <button onClick={handleStartGame}>Start Game</button>
      ) : (
        <>
          <p>Game has started!</p>
          <h3>Your Cards:</h3>
          <ul>
            {cards
              .find((c) => c.playerId === socket.id)
              ?.cards.map((card, i) => (
                <li key={i}>{card}</li>
              ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default RoomPage;
