import { useEffect } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:3001'); // подключаемся к серверу

function App() {
  useEffect(() => {
    socket.on('connect', () => {
      console.log('🟢 Подключено к серверу:', socket.id);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h1>Добро пожаловать в покер!</h1>
    </div>
  );
}

export default App;
