// RoomPage.js
import { useParams } from 'react-router-dom';
import { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import Card from '../components/Card';

function RoomPage() {
  const { roomId } = useParams();
  const [players, setPlayers] = useState([]);
  const [cards, setCards] = useState([]);
  const [statusMessage, setStatusMessage] = useState('');
  const [playerName, setPlayerName] = useState(''); 
  const [showModal, setShowModal] = useState(true);
  const [tempName, setTempName] = useState('');
  const [myId, setMyId] = useState(null);
  const [board, setBoard] = useState([]);

  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const [pot, setPot] = useState(0);
  const [currentBet, setCurrentBet] = useState(0);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  
  const socketRef = useRef();

  const sendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    socketRef.current.emit('chat_message', {
      roomId,
      sender: playerName,
      text: newMessage
    });
    setNewMessage('');
  };

  const handleNameSubmit = (e) => {
    e.preventDefault();
    const name = tempName.trim();
    if (name) {
      setPlayerName(name);
      setShowModal(false);
    }
  };

  const isHost = players[0]?.id === myId;

  const handleStartGame = () => {
    const socket = socketRef.current;
    socket.emit('start_game', { roomId });
    socket.emit('init_preflop', { roomId });
  };

  useEffect(() => {
    const socket = io('http://localhost:3001');
    socketRef.current = socket;

    socket.on('connect', () => setMyId(socket.id));
    socket.on('room_players', setPlayers);
    socket.on('preflop_started', ({ players, pot, currentBet, currentPlayerId }) => {
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Pre-flop started');
    });
    socket.on('betting_update', ({ players, pot, currentBet, currentPlayerId }) => {
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
    });
    socket.on('game_started', ({ cards }) => {
      setCards(cards);
      setStatusMessage('Game has started!');
    });
    socket.on('error_message', ({ message }) => setStatusMessage(message));

    return () => socket.disconnect();
  }, []);

  useEffect(() => {
    const socket = socketRef.current;
    socket.on('chat_message', (message) => setMessages(prev => [...prev, message]));
    return () => socket.off('chat_message');
  }, []);

  useEffect(() => {
    const chatContainer = document.querySelector('[data-chat]');
    if (chatContainer) chatContainer.scrollTop = chatContainer.scrollHeight;
  }, [messages]);

  useEffect(() => {
    const socket = socketRef.current;
    if (!playerName || !myId) return;
    socket.emit('join_room', { roomId, playerName });
    socket.emit('request_room_players', { roomId });
  }, [playerName, myId, roomId]);

  useEffect(() => {
    const socket = socketRef.current;
    socket.on('flop', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Flop dealt');
    });
    socket.on('turn', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Turn dealt');
    });
    socket.on('river', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('River dealt');
    });
    socket.on('showdown', ({ board, winners }) => {
      setBoard(board);
      if (winners.length === 1) {
        setStatusMessage(`Winner: ${winners[0].name}`);
      } else {
        setStatusMessage(`Winners: ${winners.map(w => w.name).join(', ')}`);
      }
    });

    return () => {
      socket.off('flop');
      socket.off('turn');
      socket.off('river');
      socket.off('showdown');
    };
  }, []);

  const myCards = players.find(p => p.id === myId)?.cards || [];

  return (
    <div className="p-6 font-sans text-gray-800">
      {showModal ? (
        <div className="max-w-md mx-auto p-6 bg-white rounded-xl shadow-md space-y-4">
          <h3 className="text-xl font-bold">Enter your name</h3>
          <form onSubmit={handleNameSubmit} className="space-y-2">
            <input
              type="text"
              value={tempName}
              onChange={e => setTempName(e.target.value)}
              required
              autoFocus
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-400"
            />
            <button
              type="submit"
              className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
            >
              Join
            </button>
          </form>
        </div>
      ) : (
        <div className="space-y-6">
          <div>
            <h2 className="text-2xl font-semibold">Room: {roomId}</h2>
            <h3 className="text-lg mt-2">Players:</h3>
            <ul className="list-disc pl-5">
              {players.map(p => <li key={p.id}>{p.name}</li>)}
            </ul>
          </div>

          <div className="space-y-2">
            <p><strong>Pot:</strong> {pot}</p>
            <p><strong>Current Bet:</strong> {currentBet}</p>
          </div>

          <div className="relative min-h-[400px] border-2 border-dashed border-gray-400 rounded-xl p-6 bg-green-50">
            <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
              {board.length > 0 && (
                <div className="flex gap-4 mb-4 justify-center">
                  {board.map((card, i) => <Card key={i} value={card} />)}
                </div>
              )}
            </div>

            {myCards.length > 0 && (
              <div className="mt-4">
                <h3 className="font-semibold">Your Cards:</h3>
                <div className="flex gap-4 mt-2">
                  {myCards.map((v,i) => <Card key={i} value={v} />)}
                </div>
              </div>
            )}
          </div>

          <div className="space-x-2">
            {isHost && !statusMessage && (
              <button onClick={handleStartGame} className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">
                Start Game
              </button>
            )}
            {isHost && statusMessage === 'Pre-flop started' && (
              <button onClick={() => socketRef.current.emit('deal_flop', { roomId })} className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded">
                Deal Flop
              </button>
            )}
            {isHost && statusMessage === 'Flop dealt' && (
              <button onClick={() => socketRef.current.emit('deal_turn', { roomId })} className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded">
                Deal Turn
              </button>
            )}
            {isHost && statusMessage === 'Turn dealt' && (
              <button onClick={() => socketRef.current.emit('deal_river', { roomId })} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">
                Deal River
              </button>
            )}
            {isHost && statusMessage === 'River dealt' && (
              <button onClick={() => socketRef.current.emit('showdown', { roomId })} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded">
                Showdown
              </button>
            )}
          </div>

          {statusMessage && <p className="text-lg font-medium">{statusMessage}</p>}

          <div className="bg-gray-100 rounded-lg p-4 max-h-[200px] overflow-y-auto" data-chat>
            {messages.map((msg, i) => (
              <div key={i}>
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>

          <form onSubmit={sendMessage} className="flex gap-2 mt-2">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type a message..."
              className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-400"
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
            >
              Send
            </button>
          </form>
        </div>
      )}
    </div>
  );
}

export default RoomPage;
