import { useParams } from 'react-router-dom';
import { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import Card from '../components/Card';

function RoomPage() {
  const { roomId } = useParams();
  const [players, setPlayers] = useState([]);
  const [cards, setCards] = useState([]);
  const [statusMessage, setStatusMessage] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [showModal, setShowModal] = useState(true);
  const [tempName, setTempName] = useState('');
  const [myId, setMyId] = useState(null);
  const [board, setBoard] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [pot, setPot] = useState(0);
  const [currentBet, setCurrentBet] = useState(0);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);

  const socketRef = useRef();

  // –°—Ç–∏–ª–∏ –≤ JavaScript –æ–±—ä–µ–∫—Ç–µ
  const styles = {
    primaryColor: '#2c3e50',
    accentColor: '#27ae60',
    chipColor: '#f1c40f',
    background: '#f5f6fa',
    pokerTable: '#2ecc71'
  };

  useEffect(() => {
    const socket = io('http://localhost:3001');
    socketRef.current = socket;

    socket.on('connect', () => setMyId(socket.id));
    socket.on('room_players', setPlayers);
    
    // –û—Å—Ç–∞–ª—å–Ω—ã–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π...

    return () => socket.disconnect();
  }, []);

  // –û—Å—Ç–∞–ª—å–Ω—ã–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏...

  return (
    <div className="container">
      {showModal ? (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Enter Your Name</h2>
            <form onSubmit={handleNameSubmit} style={{ margin: '20px 0' }}>
              <input
                type="text"
                value={tempName}
                onChange={e => setTempName(e.target.value)}
                placeholder="Player name"
                autoFocus
                style={{ width: '80%' }}
              />
              <button type="submit" style={{ marginTop: '15px' }}>
                Join Game
              </button>
            </form>
          </div>
        </div>
      ) : (
        <div>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* –ë–ª–æ–∫ –∏–≥—Ä–æ–∫–æ–≤ */}
            <div>
              <h2>Room: {roomId}</h2>
              <div style={{ 
                background: 'white', 
                padding: '15px', 
                borderRadius: '10px' 
              }}>
                <h3>Players ({players.length})</h3>
                {players.map(p => (
                  <div 
                    key={p.id} 
                    style={{
                      padding: '8px',
                      margin: '5px',
                      background: p.id === myId ? '#e8f4f8' : '#f5f6fa',
                      borderRadius: '5px'
                    }}
                  >
                    {p.name} {p.id === myId && '(You)'}
                    {p.stack && ` - $${p.stack}`}
                  </div>
                ))}
              </div>
            </div>

            {/* –ß–∞—Ç */}
            <div className="chat-container">
              <div className="chat-messages">
                {messages.map((msg, i) => (
                  <div key={i} style={{ margin: '5px 0' }}>
                    <strong style={{ color: styles.primaryColor }}>{msg.sender}:</strong>
                    <span style={{ marginLeft: '5px' }}>{msg.text}</span>
                  </div>
                ))}
              </div>
              <form onSubmit={sendMessage} style={{ display: 'flex' }}>
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type message..."
                  style={{ flex: 1, marginRight: '5px' }}
                />
                <button type="submit">Send</button>
              </form>
            </div>
          </div>

          {/* –ü–æ–∫–µ—Ä–Ω—ã–π —Å—Ç–æ–ª */}
          <div 
            className="poker-table"
            style={{ 
              background: styles.pokerTable,
              borderRadius: '50%',
              width: '800px',
              height: '400px',
              margin: '20px auto',
              position: 'relative',
              boxShadow: '0 0 30px rgba(0, 0, 0, 0.2)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <div className="community-cards">
              {board.map((card, i) => (
                <Card key={i} value={card} />
              ))}
            </div>
          </div>

          {/* –ö–∞—Ä—Ç—ã –∏–≥—Ä–æ–∫–∞ */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            margin: '20px',
            gap: '10px'
          }}>
            {myCards.map((v, i) => (
              <Card key={i} value={v} style={{ transform: `rotate(${i*5}deg)` }} />
            ))}
          </div>

          {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
          <div style={{ 
            background: 'white',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center'
          }}>
            <h3>Pot: ${pot}</h3>
            <p>Current Bet: ${currentBet}</p>
            
            <div style={{ margin: '15px 0' }}>
              {isHost && !statusMessage && (
                <button 
                  onClick={handleStartGame} 
                  style={{ background: styles.accentColor }}
                >
                  üéÆ Start Game
                </button>
              )}
              
              {statusMessage && (
                <div style={{ 
                  padding: '10px',
                  background: '#e8f4f8',
                  borderRadius: '5px',
                  margin: '10px'
                }}>
                  {statusMessage}
                </div>
              )}
            </div>

            <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
              <button>Fold</button>
              <button>Call</button>
              <button>Raise</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default RoomPage;
