// client/src/pages/JoinPage.jsx
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function JoinPage() {
  const [rooms, setRooms] = useState([]);
  const [roomCode, setRoomCode] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:3001/public-rooms')
      .then(res => res.json())
      .then(data => setRooms(data));
  }, []);

  const joinRoom = (roomId) => navigate(`/room/${roomId}`);
  const handlePrivateJoin = () => navigate(`/room/${roomCode}`);

  return (
    <div>
      <h2>Join a Public Room</h2>
      {rooms.map(id => (
        <div key={id}>
          <button onClick={() => joinRoom(id)}>Join Room {id}</button>
        </div>
      ))}
      <h3>Join Private Room</h3>
      <input value={roomCode} onChange={e => setRoomCode(e.target.value)} placeholder="Enter room code" />
      <button onClick={handlePrivateJoin}>Join</button>
    </div>
  );
}

export default JoinPage;
