// client/src/pages/JoinRoomPage.jsx
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function JoinRoomPage() {
  const [roomId, setRoomId] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleJoin = async () => {
    try {
      const response = await axios.post('http://localhost:3001/join-room', {
        roomId,
        password,
      });

      // если всё хорошо — переходим на /room/:roomId
      navigate(`/room/${roomId}`);
    } catch (err) {
      alert(err.response?.data?.message || 'Error joining room');
    }
  };

  return (
    <div>
      <h2>Join a Room</h2>
      <input
        type="text"
        placeholder="Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password (if private)"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleJoin}>Join Game</button>
    </div>
  );
}

export default JoinRoomPage;
