import { useParams } from 'react-router-dom'; 
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:3001');

function RoomPage() {
  const { roomId } = useParams();
  const [players, setPlayers] = useState([]);
  const [cards, setCards] = useState([]);
  const [statusMessage, setStatusMessage] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [showModal, setShowModal] = useState(true);
  const [tempName, setTempName] = useState('');
  const [myId, setMyId] = useState(null);

  useEffect(() => {
    socket.on('connect', () => {
      setMyId(socket.id);
    });

    return () => {
      socket.off('connect');
    };
  }, []);

  useEffect(() => {
    if (!playerName || !myId) return;

    socket.emit('join_room', { roomId, playerName });

    socket.on('room_players', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socket.on('game_started', ({ cards }) => {
      setCards(cards);
      setStatusMessage('Game has started!');
    });

    socket.emit('request_room_players', { roomId });

    return () => {
      socket.off('room_players');
      socket.off('game_started');
    };
  }, [roomId, playerName, myId]);

  const myCards = cards.find(c => c.playerId === myId)?.cards || [];

  const handleNameSubmit = (e) => {
    e.preventDefault();
    if (tempName.trim()) {
      setPlayerName(tempName.trim());
      setShowModal(false);
    }
  };

  const handleStartGame = () => {
    socket.emit('start_game', { roomId });
  };

  const isHost = players.find(p => p.id === myId)?.isHost;

  return (
    <div>
      {showModal && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <h3>Enter your name</h3>
            <form onSubmit={handleNameSubmit}>
              <input
                type="text"
                value={tempName}
                onChange={(e) => setTempName(e.target.value)}
                autoFocus
                required
              />
              <button type="submit">Join</button>
            </form>
          </div>
        </div>
      )}

      {!showModal && (
        <>
          <h2>Room: {roomId}</h2>

          <h3>Players:</h3>
          <ul>
            {players.map(p => (
              <li key={p.id}>
                {p.name} {p.id === myId && '(You)'} {p.isHost && '‚≠ê'}
              </li>
            ))}
          </ul>

          {isHost && (
            <button onClick={handleStartGame} disabled={players.length < 2}>
              Start Game
            </button>
          )}

          {statusMessage && <p>{statusMessage}</p>}

          {myCards.length > 0 && (
            <div>
              <h3>Your Cards:</h3>
              <ul>
                {myCards.map((card, i) => <li key={i}>{card}</li>)}
              </ul>
            </div>
          )}
        </>
      )}
    </div>
  );
}

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0, left: 0,
    width: '100vw', height: '100vh',
    backgroundColor: 'rgba(0,0,0,0.6)',
    display: 'flex', alignItems: 'center', justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    background: '#fff',
    padding: '2rem',
    borderRadius: '10px',
    textAlign: 'center',
  },
};

export default RoomPage;
