import { useEffect, useState } from 'react';

function App() {
  const [message, setMessage] = useState('Проверка соединения...');
  const [roomStatus, setRoomStatus] = useState(''); // Статус комнаты

  useEffect(() => {
    fetch('http://localhost:3001')
      .then(res => res.text())
      .then(data => {
        setMessage(data); // Выводим ответ сервера
      })
      .catch(err => {
        setMessage('Ошибка подключения к серверу');
        console.error('Ошибка:', err);
      });
  }, []);

  // Функция для создания комнаты
const createRoom = async () => {
  const response = await fetch('http://localhost:3001/create-room', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ /* данные для создания комнаты */ }),
  });
  const data = await response.json();
  console.log(data.message); // Печатаем ответ от сервера
};

// Функция для присоединения к комнате
const joinRoom = async () => {
  const response = await fetch('http://localhost:3001/join-room', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ /* данные для присоединения к комнате */ }),
  });
  const data = await response.json();
  console.log(data.message); // Печатаем ответ от сервера
};

  return (
    <div>
      <h1>Добро пожаловать в покер!</h1>
      <p>{message}</p>
      <div>
        <button onClick={createRoom}>Создать комнату</button>
        <button onClick={joinRoom}>Присоединиться к комнате</button>
      </div>
      <p>{roomStatus}</p>
    </div>
  );
}

export default App;
