import { io } from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io('http://localhost:3001'); // заменить при деплое

function RoomPage({ roomId, playerName }) {
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    socket.emit('join_room', { roomId, playerName });

    socket.on('room_players', (players) => {
      setPlayers(players);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h2>Room: {roomId}</h2>
      <h3>Players:</h3>
      <ul>
        {players.map((p) => (
          <li key={p.id}>{p.name}</li>
        ))}
      </ul>
    </div>
  );
}
