// components/Card.jsx
import React from 'react';
import './Card.css';

const Card = ({ value }) => {
  if (!value) return null;
  
  const suit = value.slice(-1);
  const rank = value.slice(0, -1);
  const isRed = ['H', 'D'].includes(suit);
  const isFaceCard = ['J', 'Q', 'K'].includes(rank);

  return (
    <div className={`card ${isRed ? 'red' : 'black'}`} data-rank={rank}>
      <div className="corner top-left">
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbols[suit]}</div>
      </div>
      
      {isFaceCard ? (
        <div className="face-design">
          <div className={`royal-symbol ${rank.toLowerCase()}`}></div>
        </div>
      ) : (
        <div className="center">
          {Array.from({ length: getSymbolCount(rank) }).map((_, i) => (
            <div key={i} className="symbol">{suitSymbols[suit]}</div>
          ))}
        </div>
      )}

      <div className="corner bottom-right">
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbols[suit]}</div>
      </div>
    </div>
  );
};
