import { useParams } from 'react-router-dom';
import { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import Card from '../components/Card';

// Styling for the RoomPage
const pageStyles = {
  fontFamily: 'Arial, sans-serif',
  padding: '20px',
  backgroundColor: '#f4f6f6',
  color: '#2c3e50',
  minHeight: '100vh',
};

const containerStyles = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
};

const cardContainerStyles = {
  display: 'flex',
  gap: '10px',
  marginBottom: '20px',
  justifyContent: 'center',
};

const buttonStyles = {
  padding: '10px 20px',
  backgroundColor: '#3498db',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '16px',
  marginTop: '20px',
};

const formStyles = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  gap: '10px',
};

const inputStyles = {
  padding: '10px',
  fontSize: '16px',
  border: '1px solid #ddd',
  borderRadius: '5px',
  width: '250px',
};

const chatContainerStyles = {
  backgroundColor: '#ffffff',
  borderRadius: '5px',
  padding: '10px',
  maxHeight: '200px',
  overflowY: 'scroll',
  marginBottom: '10px',
};

const messageStyles = {
  marginBottom: '5px',
};

const statusStyles = {
  fontSize: '18px',
  fontWeight: 'bold',
  marginTop: '20px',
};

const RoomPage = () => {
  const { roomId } = useParams();
  const [players, setPlayers] = useState([]);
  const [cards, setCards] = useState([]);
  const [statusMessage, setStatusMessage] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [showModal, setShowModal] = useState(true);
  const [tempName, setTempName] = useState('');
  const [myId, setMyId] = useState(null);
  const [board, setBoard] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [pot, setPot] = useState(0);
  const [currentBet, setCurrentBet] = useState(0);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  
  const socketRef = useRef();
  
  const sendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    socketRef.current.emit('chat_message', {
      roomId,
      sender: playerName,
      text: newMessage
    });
    setNewMessage('');
  };

  const handleNameSubmit = (e) => {
    e.preventDefault();
    const name = tempName.trim();
    if (name) {
      setPlayerName(name);
      setShowModal(false);
    }
  };

  const isHost = players[0]?.id === myId;

  const handleStartGame = () => {
    const socket = socketRef.current;
    socket.emit('start_game', { roomId });
    socket.emit('init_preflop', { roomId });
  };

  useEffect(() => {
    const socket = io('http://localhost:3001');
    socketRef.current = socket;

    socket.on('connect', () => setMyId(socket.id));

    socket.on('room_players', setPlayers);

    socket.on('preflop_started', ({ players, pot, currentBet, currentPlayerId }) => {
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Pre-flop started');
    });

    socket.on('betting_update', ({ players, pot, currentBet, currentPlayerId }) => {
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
    });

    socket.on('game_started', ({ cards }) => {
      setCards(cards);
      setStatusMessage('Game has started!');
    });

    socket.on('error_message', ({ message }) => setStatusMessage(message));

    return () => socket.disconnect();
  }, []);

  useEffect(() => {
    const socket = socketRef.current;
    socket.on('chat_message', (message) => setMessages(prev => [...prev, message]));
    return () => socket.off('chat_message');
  }, []);

  useEffect(() => {
    const chatContainer = document.querySelector('[style*="height: 200px"]');
    if (chatContainer) chatContainer.scrollTop = chatContainer.scrollHeight;
  }, [messages]);

  useEffect(() => {
    const socket = socketRef.current;
    if (!playerName || !myId) return;
    socket.emit('join_room', { roomId, playerName });
    socket.emit('request_room_players', { roomId });
  }, [playerName, myId, roomId]);

  useEffect(() => {
    const socket = socketRef.current;
    socket.on('flop', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Flop dealt');
    });
    socket.on('turn', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('Turn dealt');
    });
    socket.on('river', ({ board, players, pot, currentBet, currentPlayerId }) => {
      setBoard(board);
      setPlayers(players);
      setPot(pot);
      setCurrentBet(currentBet);
      setCurrentPlayerId(currentPlayerId);
      setStatusMessage('River dealt');
    });
    socket.on('showdown', ({ board, winners }) => {
      setBoard(board);
      if (winners.length === 1) {
        setStatusMessage(`Winner: ${winners[0].name}`);
      } else {
        setStatusMessage(`Winners: ${winners.map(w => w.name).join(', ')}`);
      }
    });

    return () => {
      socket.off('flop');
      socket.off('turn');
      socket.off('river');
      socket.off('showdown');
    };
  }, []);

  const myCards = players.find(p => p.id === myId)?.cards || [];

  return (
    <div style={pageStyles}>
      {showModal ? (
        <div style={containerStyles}>
          <h3>Enter your name</h3>
          <form onSubmit={handleNameSubmit} style={formStyles}>
            <input
              type="text"
              value={tempName}
              onChange={e => setTempName(e.target.value)}
              required
              autoFocus
              style={inputStyles}
            />
            <button type="submit" style={buttonStyles}>Join</button>
          </form>
        </div>
      ) : (
        <div style={containerStyles}>
          <h2>Room: {roomId}</h2>
          <h3>Players:</h3>
          <ul>
            {players.map(p => <li key={p.id}>{p.name}</li>)}
          </ul>
          <p>Pot: {pot}</p>
          <p>Current Bet: {currentBet}</p>

          {/* Игровой стол */}
          <div style={{ 
            position: 'relative', 
            minHeight: '400px', 
            border: '2px dashed #7f8c8d',
            borderRadius: '10px',
            padding: '20px',
            marginTop: '20px',
          }}>
            {/* Общие карты */}
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
              {board.length > 0 && (
                <div style={cardContainerStyles}>
                  {board.map((card, i) => <Card key={i} value={card} />)}
                </div>
              )}
            </div>

            {/* Карты текущего игрока */}
            {myCards.length > 0 && (
              <div>
                <h3>Your Cards:</h3>
                <div className="your-cards" style={{ display: 'flex', padding: '1rem 0' }}>
                  {myCards.map((v,i) => <Card key={i} value={v} />)}
                </div>
              </div>
            )}
          </div>

          {/* Игровые действия */}
          <div>
            {isHost && !statusMessage && <button onClick={handleStartGame} style={buttonStyles}>Start Game</button>}
            {statusMessage && <p style={statusStyles}>{statusMessage}</p>}
            {isHost && statusMessage === 'Pre-flop started' && (
              <button onClick={() => socketRef.current.emit('deal_flop', { roomId })} style={buttonStyles}>Deal Flop</button>
            )}
            {isHost && statusMessage === 'Flop dealt' && (
              <button onClick={() => socketRef.current.emit('deal_turn', { roomId })} style={buttonStyles}>Deal Turn</button>
            )}
            {isHost && statusMessage === 'Turn dealt' && (
              <button onClick={() => socketRef.current.emit('deal_river', { roomId })} style={buttonStyles}>Deal River</button>
            )}
            {isHost && statusMessage === 'River dealt' && (
              <button onClick={() => socketRef.current.emit('showdown', { roomId })} style={buttonStyles}>Showdown</button>
            )}
          </div>

          {/* Чат */}
          <div style={chatContainerStyles}>
            {messages.map((msg, i) => (
              <div key={i} style={messageStyles}>
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <form onSubmit={sendMessage}>
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type a message..."
              style={inputStyles}
            />
            <button type="submit" style={buttonStyles}>Send</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default RoomPage;
