import { useEffect, useState } from 'react';

function App() {
  const [message, setMessage] = useState('Connecting to server');
  const [roomStatus, setRoomStatus] = useState('');
  const [roomId, setRoomId] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001')
      .then(res => res.text())
      .then(data => setMessage(data))
      .catch(err => {
        setMessage('Failed to connect to server');
        console.error('Error:', err);
      });
  }, []);

  // Создание комнаты
  const createRoom = async () => {
    try {
      const response = await fetch('http://localhost:3001/create-room', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      const data = await response.json();
      setRoomId(data.roomId);
      setRoomStatus(data.message + ' ID of the room ' + data.roomId);
    } catch (err) {
      setRoomStatus('Failed to create the room');
      console.error(err);
    }
  };

  // Присоединение к комнате
  const joinRoom = async () => {
    if (!roomId) {
      setRoomStatus('Enter ID to join the room');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/join-room', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ roomId }),
      });
      const data = await response.json();
      setRoomStatus(data.message);
    } catch (err) {
      setRoomStatus('Failed to join to the room');
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Welcome to Poker!</h1>
      <p>{message}</p>

      <div>
        <button onClick={createRoom}>Create room</button>
        <button onClick={joinRoom}>Join room</button>
      </div>

      <div>
        <input
          placeholder="Enter ID of the room"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
      </div>

      <p>{roomStatus}</p>
    </div>
  );
}

export default App;
